mapper.update:
update plant_activity set title = #{title,jdbcType=VARCHAR}, sub_title = #{subTitle,jdbcType=VARCHAR}, open_time = #{openTime,jdbcType=TIMESTAMP}, close_time = #{closeTime,jdbcType=TIMESTAMP}, pic_url = #{picUrl,jdbcType=VARCHAR}, link_url = #{linkUrl,jdbcType=VARCHAR},
content = CONCAT("<style>img{max-width:100%!important;}</style>",#{content,jdbcType=LONGVARCHAR})
where id = #{id,jdbcType=INTEGER}

mapper.select:
select  from plant_activity



    title like #{titleLike}


order by id desc
