mapper.insert:
insert into plant_article (id, title, type_id, 
  seo_key, publish_time, is_show, 
  pic_url,thumb_pic_url, read_count, add_time,is_recommend,summary
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{typeId,jdbcType=INTEGER}, 
  #{seoKey,jdbcType=VARCHAR}, #{publishTime,jdbcType=TIMESTAMP}, #{isShow,jdbcType=BIT},
  #{picUrl,jdbcType=VARCHAR}, #{thumbPicUrl,jdbcType=VARCHAR}, #{readCount,jdbcType=INTEGER}, #{addTime,jdbcType=TIMESTAMP},
  #{isRecommend,jdbcType=INTEGER},#{summary,jdbcType=VARCHAR}
  )

mapper.select:
select t1.id, title, type_id, seo_key, publish_time, is_show, pic_url,thumb_pic_url, read_count, add_time,is_recommend,summary,
t2.name as type_name,t2.url_name as type_url_name
 from plant_article t1
 left join plant_article_type t2 on t1.type_id = t2.id



    title = #{titleLike}


    AND type_id = #{typeId}


    AND is_show = #{isShow}


    AND is_recommend = #{isRecommend}


order by add_time desc ,publish_time desc

select ,content from plant_article t1
left join plant_article_content t2 on t1.id = t2.article_id
where id = #{id}
