mapper.insert:
insert into plant_banner (id, `type`, `name`, pic_url, 
  thumb_pic_url, jump_url, is_show, 
  sort_order, show_time, add_time
  )
values (#{id,jdbcType=INTEGER}, #{type,jdbcType=BIT}, #{name,jdbcType=VARCHAR}, #{picUrl,jdbcType=VARCHAR}, 
  #{thumbPicUrl,jdbcType=VARCHAR}, #{jumpUrl,jdbcType=VARCHAR}, #{isShow,jdbcType=INTEGER},
  #{sortOrder,jdbcType=TINYINT}, #{showTime,jdbcType=TIMESTAMP}, #{addTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select   from plant_banner


      id = #{id}


      AND name like #{name}


      AND is_show = #{isShow}


order by sort_order desc
