mapper.select:
select t1.* , t2.name as seed_name,t3.class_name as seed_type_name,t2.grow_days as seed_grow_days ,t2.per_price as seed_price from plant_seed_order_detail t1 left join plant_seed t2 on t1.seed_id = t2.id left join plant_seed_type t3 on t2.seed_type = t3.id
t1.user_id = #{userId}
AND t1.state = #{state}
order by t1.pay_time desc,t1.add_time desc

select sum(buy_count) from plant_seed_order_detail
where seed_id = #{seedId}

  AND state = #{state}

select t2.id as blockId from plant_seed_order_detail t1 left join plant_seed t2 on t1.seed_id = t2.id left join plant_block t3 on t2.seed_type = t3.seed_type where t1.state = 1
and t3.left_count >= 0
and t3.is_show = 1 and t3.state = 1 and t3.is_delete = 0 and t1.id = #{id}

select t1.*, t2.name as seed_name,t3.class_name as seed_type_name,t2.grow_days as seed_grow_days, t1.pay_time , t2.per_price as seed_price,t4.pic_url_thumb as pic_url from plant_seed_order_detail t1 left join plant_seed t2 on t1.seed_id = t2.id left join plant_seed_type t3 on t2.seed_type = t3.id left join plant_seed_pic t4 on t2.pic_ids = t4.id where t1.user_id = #{userId}
AND t1.state < 2
order by t1.add_time desc

select t1.*, t2.name as seed_name,t3.class_name as seed_type_name,t2.grow_days as seed_grow_days, t1.pay_time ,t1.add_time,
t2.per_price as seed_price,t4.phone,t4.real_name
from plant_seed_order_detail t1
left join plant_seed t2 on t1.seed_id = t2.id
left join plant_seed_type t3 on t2.seed_type = t3.id
left join plant_user t4 on t1.user_id = t4.id



    t4.phone like #{phoneLike}


    AND t3.class_name = #{wq.seedTypeName}



    AND t4.real_name like #{realNameLike}


    AND t1.state = #{wq.state}


    AND t1.order_number = #{wq.orderNumber}



        AND UNIX_TIMESTAMP(#{wq.startTime}) <= UNIX_TIMESTAMP(t1.add_time)
        AND UNIX_TIMESTAMP(#{wq.endTime}) >= UNIX_TIMESTAMP(t1.add_time)



    AND t3.is_for_new = #{wq.isForNew}



order by t1.add_time desc
