mapper.update:
update plant_user_address
set is_default = 0
where user_id = #{userId}

    and id <> #{id}

update plant_user_address
set is_default = 1
where user_id = #{userId} and id = #{id}

mapper.select:
select  from plant_user_address
where id = #{id} and user_id = #{userId}

  and is_delete = #{isDelete}
