mapper.select:
select t1.id, t1.user_id, t1.fertilizer_id, t1.`state`,
t1.use_order_number,t1.add_time,t1.start_time,t1.end_time,t1.fertilizer_amount,
t3.type_name,t4.phone,t4.real_name
from plant_user_fertilizer t1
left join plant_fertilizer t2 on t1.fertilizer_id = t2.id
left join plant_fertilizer_type t3 on t2.type_id = t3.id
left join plant_user t4 on t1.user_id = t4.id


        t1.id = #{id}


        AND t1.user_id = #{userId}


        AND t1.fertilizer_id = #{fertilizerId}


        AND t1.state = #{state}


        AND t4.phone = #{phone}


        AND t4.real_name = #{realName}


        AND t1.use_order_number = #{useOrderNumber}



order by id desc

select t1.id,t1.state,t2.type_id,t2.amount_min,t2.amount_max,t1.start_time,t1.fertilizer_amount, t1.end_time as expire_time,t2.use_no_limit,t4.number as use_in_block_number ,t5.name as use_in_product_name, t6.class_name as use_in_seed_type_name,t3.type_name, t2.use_in_product as use_in_single_product, t2.use_in_seed_type as use_in_single_seed, t2.use_in_block as use_in_single_block, t1.fertilizer_id from plant_user_fertilizer t1 left join plant_fertilizer t2 on t1.fertilizer_id = t2.id left join plant_fertilizer_type t3 on t2.type_id = t3.id left join plant_block t4 on t2.use_in_block = t4.id left join plant_mall_product t5 on t2.use_in_product = t5.id left join plant_seed_type t6 on t2.use_in_seed_type = t6.id
t1.user_id = #{userId}
AND (t2.use_in_product = #{productId} OR t2.use_in_product = 0)
AND (t2.use_in_block = #{blockId} OR t2.use_in_block = 0)
AND (t2.use_in_seed_type = #{seedTypeId} OR t2.use_in_seed_type = 0)
AND ( (#{amount} >= t2.amount_min AND #{amount} <= t2.amount_max AND t2.amount_max is not null) OR (#{amount} >= t2.amount_min AND t2.amount_max is null) )
AND UNIX_TIMESTAMP(t2.start_time) <= UNIX_TIMESTAMP(#{expireTime}) AND UNIX_TIMESTAMP(t2.expire_time) >= UNIX_TIMESTAMP(#{expireTime})
AND t1.state = #{state}
AND t2.type_id = #{type}
AND t2.type_id in
#{item}
order by t1.state,t1.id desc

mapper.update:
