mapper.select:
select t1.id, t1.user_id, t1.fertilizer_id, t1.`state`, t1.use_order_number,t1.add_time,t1.start_time,t1.end_time,t1.fertilizer_amount, t3.type_name,t4.phone,t4.real_name, t1.use_time from plant_user_fertilizer t1 left join plant_fertilizer t2 on t1.fertilizer_id = t2.id left join plant_fertilizer_type t3 on t2.type_id = t3.id left join plant_user t4 on t1.user_id = t4.id
t1.id = #{wq.id}
and t1.user_id = #{wq.userId}
and t1.fertilizer_id = #{wq.fertilizerId}
and t1.state = #{wq.state}
and instr(t4.phone, #{wq.phone})
and instr(t4.real_name, #{wq.realName})
and instr(t1.use_order_number, #{wq.useOrderNumber})
order by t1.id desc

select t1.id, t1.fertilizer_id, t2.type_id, t3.type_name, t1.fertilizer_amount, t2.amount_min, t2.amount_max, t1.start_time, t1.end_time as expire_time, t1.state, t2.use_in_block as use_in_single_block, t4.number as use_in_block_number, t2.use_in_product as use_in_single_product, t5.name as use_in_product_name, t2.use_in_seed_type as use_in_single_seed, t6.class_name as use_in_seed_type_name from plant_user_fertilizer t1 left join plant_fertilizer t2 on t1.fertilizer_id = t2.id left join plant_fertilizer_type t3 on t2.type_id = t3.id left join plant_block t4 on t2.use_in_block = t4.id left join plant_mall_product t5 on t2.use_in_product = t5.id left join plant_seed_type t6 on t2.use_in_seed_type = t6.id
t1.user_id = #{wq.userId}
and (t2.use_in_product = #{wq.productId} or t2.use_in_product = 0)
and (t2.use_in_block = #{wq.blockId} or t2.use_in_block = 0)
and (t2.use_in_seed_type = #{wq.seedTypeId} or t2.use_in_seed_type = 0)
and ( ( (t2.amount_max is not null) and (#{wq.amount} between t2.amount_min and t2.amount_max) ) or ( (t2.amount_max is null) and (t2.amount_min <= #{wq.amount}) ) )
and UNIX_TIMESTAMP(t2.start_time) <= UNIX_TIMESTAMP(#{wq.expireTime}) and UNIX_TIMESTAMP(t2.expire_time) >= UNIX_TIMESTAMP(#{wq.expireTime})
AND t1.state = #{wq.state}
AND t2.type_id = #{wq.type}
AND t2.type_id in
#{item}
order by t1.state, t1.id desc

