mapper.insert:
insert into plant_user_message (id,title, message, user_id,
  is_read, is_delete, add_time
  )
values (#{id,jdbcType=INTEGER},#{title,jdbcType=VARCHAR}, #{message,jdbcType=VARCHAR}, #{userId,jdbcType=INTEGER},
  #{isRead,jdbcType=BIT}, #{isDelete,jdbcType=BIT}, #{addTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select t1.id,title,message,is_read,t1.add_time ,t2.phone as phone
from plant_user_message t1
left join plant_user t2 on t1.user_id = t2.id
where is_delete = #{isDelete}
AND t1.user_id = #{userId}
order by add_time desc

mapper.update:
update plant_user_message


    is_read = #{isRead},


    is_delete = #{isDelete},




    user_id = #{userId}


    AND id = #{id}
