mapper.select:
select t4.name productName, t4.has_count productCount from plant_user_money_log t1 left join plant_mall_order t2 on t1.detail_id = t2.order_number left join plant_mall_order_detail t3 on t2.id = t3.id left join plant_mall_product t4 on t3.product_id = t4.id
t1.detail_id = #{detailId}
group by `name`

select t3.class_name productName, t2.buy_count productCount from plant_user_money_log t1 left join plant_seed_order_detail t2 on t1.detail_id = t2.order_number left join plant_seed_type t3 on t2.seed_id = t3.id
t1.detail_id = #{detailId}
group by t3.class_name

select t3.class_name productName, t2.plant_count productCount from plant_user_money_log t1 left join plant_reap t2 on t1.detail_id = t2.order_number left join plant_seed_type t3 on t2.seed_type = t3.id
t1.detail_id = #{detailId}
group by t3.class_name

select t3.real_name productName from plant_user_money_log t1 left join plant_reap t2 on t1.detail_id = t2.order_number left join plant_user t3 on t2.user_id = t3.id
t1.detail_id = #{detailId}
group by real_name

select IFNULL(sum(ABS(affect_money)), 0)
from plant_user_money_log

    user_id = #{userId}
    and date_format(affect_time, '%Y-%m') = date_format(#{dateTime}, '%Y-%m')
    and affect_type in

        #{id}
